mapper.select:
select * from t_company
where 1 = 1

	and uid = #{uid}

order by id DESC limit #{page},#{size}

select * from t_company where 1 = 1
and uid = #{uid}
order by id DESC

select * from t_company where 1 = 1
and uid = #{uid}
and id = #{cid} order by id desc limit 1

mapper.delete:
delete from t_company
where 1 = 1

	and uid = #{uid}

and id = #{cid}

delete from t_company
where 1 = 1

	and uid = #{uid}

mapper.select:
select * from t_company where 1 = 1
and (id = #{search} or name like concat('%', #{search}, '%'))
order by id DESC limit #{page}, #{size}
