mapper.select:
select * from t_exchange
where 1 = 1

	and type = #{type}


	and cid = #{cid}



		and tm > #{start}




		and tm < #{end}


order by id DESC limit #{page}, #{size}

select * from t_expected_income
where tm_inactivate like concat('%', year(curdate()), '%')
and cid = #{cid}
order by id DESC limit #{page}, #{size}

select * from t_expected_income
where 1 = 1
and cid = #{cid}

	and status = #{status}



		and tm_inactivate > #{from}




		and tm_inactivate < #{to}


order by id DESC limit #{page}, #{size}

select * from t_expected_income
where tm_inactivate like concat('%', year(curdate()), '%')
and cid = #{cid}

	and uid = #{uid}

order by id DESC limit #{page}, #{size}

select * from t_expected_income
where 1 = 1
and cid = #{cid}

	and status = #{status}


	and uid = #{uid}



		and tm_inactivate > #{from}




		and tm_inactivate < #{to}


order by id DESC limit #{page}, #{size}

select * from t_expected_income
where cid = #{cid}

	and uid = #{uid}

order by tm_activate desc limit 1
