mapper.select:
select * from t_order
where cid in (select id from t_company where uid = #{uid})

	and status = #{status}

order by id DESC limit #{page}, #{size}

select count(1) from t_order
where cid in (select id from t_company where uid = #{uid})

	and status = #{status}

select a.*, b.uid, b.name from t_order a, t_company b
where b.id = a.cid and b.uid = #{uid}

	and a.status = #{status}


	and a.type = #{type}


	and a.cid = #{cid}



		and a.tm_create > #{start}




		and a.tm_create < #{end}


order by a.id DESC limit #{page}, #{size}

select count(1) from t_order a, t_company b
where b.id = a.cid and b.uid = #{uid}

	and a.status = #{status}


	and a.type = #{type}


	and a.cid = #{cid}



		and a.tm_create > #{start}




		and a.tm_create < #{end}
